// Main.jsx
import React, { useState } from 'react';
import Card from './Card';
import axios from 'axios';

const Main = () => {
  const [search, setSearch] = useState('');
  const [bookData, setData] = useState([]);

  const searchBook = (evt) => {
    if (evt.key === 'Enter') {
      axios
        .get(
          `https://www.googleapis.com/books/v1/volumes?q=${search}&key=YOUR_API_KEY&maxResults=40`
        )
        .then((res) => setData(res.data.items))
        .catch((err) => console.log(err));
    }
  };

  return (
    <>
      <div className="bg-gray-900 text-white p-8">
        <div className="max-w-3xl mx-auto">
          <h1 className="text-4xl font-bold mb-4">
            A room without books is like a body without a soul.
          </h1>
        </div>
      </div>

      <div className="bg-gray-100 py-8">
        <div className="max-w-3xl mx-auto">
          <h2 className="text-2xl font-semibold mb-2">Find Your Book</h2>
          <div className="flex items-center space-x-4">
            <input
              type="text"
              placeholder="Enter Your Book Name"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              onKeyPress={searchBook}
              className="border-2 border-gray-300 px-4 py-2 w-64 rounded-lg focus:outline-none focus:border-blue-500"
            />
            <button className="bg-blue-500 text-white px-6 py-2 rounded-full">
              <i className="fas fa-search"></i>
            </button>
          </div>
          <img src="./images/bg2.png" alt="" className="mt-8" />
        </div>
      </div>

      <div className="container mx-auto mt-8">
        <Card book={bookData} />
      </div>
    </>
  );
};

export default Main;
