// Card.jsx
import React, { useState } from 'react';
import Modal from './Modal';

const Card = ({ book }) => {
  const [show, setShow] = useState(false);
  const [bookItem, setItem] = useState(null);

  const toggleModal = (item) => {
    if (show && item === bookItem) {
      // If the modal is already open and the same card is clicked again, hide the modal
      setShow(false);
    } else {
      // Otherwise, show the modal for the clicked card
      setShow(true);
      setItem(item);
    }
  };

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
      {book.map((item) => {
        let thumbnail =
          item.volumeInfo.imageLinks && item.volumeInfo.imageLinks.smallThumbnail;
        let amount = item.saleInfo.listPrice && item.saleInfo.listPrice.amount;

        if (thumbnail !== undefined && amount !== undefined) {
          return (
            <div
              key={item.id}
              className="card bg-white border border-gray-200 rounded-lg overflow-hidden shadow-md hover:shadow-lg cursor-pointer transition duration-300 transform hover:scale-105 relative"
              onClick={() => toggleModal(item)}
            >
              <div className="relative">
                <img
                  src={thumbnail}
                  alt={item.volumeInfo.title}
                  className="w-full h-44 object-cover rounded-t-md"
                />
                <div className="overlay absolute inset-0 bg-gray-800 opacity-75 invisible group-hover:visible flex items-center justify-center">
                  <i className="fas fa-search text-white text-2xl"></i>
                </div>
              </div>
              <div className="p-4">
                <h3 className="text-xl font-semibold text-gray-800 mb-2 line-clamp-2">
                  {item.volumeInfo.title}
                </h3>
                <p className="text-gray-500 mb-2">
                  Author: {item.volumeInfo.authors?.join(', ')}
                </p>
                <p className="text-blue-500 font-semibold">&#8377;{amount}</p>
              </div>
            </div>
          );
        }
      })}
      <Modal show={show} item={bookItem} onClose={() => setShow(false)} />
    </div>
  );
};

export default Card;
